CREATE TABLE `ima_db`.`users` (`id` INT(50) NOT NULL AUTO_INCREMENT , `lastname` VARCHAR(250) NOT NULL , `firstname` VARCHAR(250) NOT NULL , `gender` VARCHAR(250) NOT NULL , `birthday` DATE NOT NULL , `birthplace` VARCHAR(250) NOT NULL , `id_card` VARCHAR(10) NOT NULL , `country` VARCHAR(50) NOT NULL , `city` VARCHAR(50) NOT NULL , `phone` VARCHAR(11) NOT NULL , `email` VARCHAR(250) NOT NULL , `password` VARCHAR(250) NOT NULL , `token` VARCHAR(10) NOT NULL , `created_at` DATETIME NOT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;